import React, { useState, useEffect } from 'react';
import './App.css';
import { UilFilePlusAlt } from '@iconscout/react-unicons'
import Popup from 'reactjs-popup'
import 'reactjs-popup/dist/index.css';


function App() {
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("");
  const [notes, setNotes] = useState([]);

const handleClick = () =>{
  try{
    fetch("http://localhost:1234/new", {
      method: "POST",
      body: JSON.stringify({
        title: title,
        description: desc,
      })
    });
  } catch(err){
    console.error(`Error creating a note: ${err}`);
  }

}

useEffect(()=>{
  try{
    fetch("http://localhost:1234/notes")
      .then((res) => res.json())
      .then((data) => {
        setNotes(data);
      });
  } catch(err){
    console.error(`Error Getting notes: ${err}`);
  }
},[]);

  return (
    <body>
      <div class="wrapper">
        <div class="note-item">
        <Popup class="popup" trigger={
            <button class="button"> 
                <UilFilePlusAlt size="225px"/>
            </button>
          } modal nested>
        {close => (
                        <div className='modal'>
                            <div className='content'>
                              <header>
                                <p>Add A New Note</p>
                              </header>
                              <form action="#">
                                <div>
                                  <label for="">Title</label>
                                  <input type="text" onChange={(e)=> setTitle(e.target.value)}/>
                                </div>
                                <div>
                                  <label for="">Description</label>
                                  <textarea onChange={(e)=>setDesc(e.target.value)}/>
                                </div>
                              </form>

                            </div>
                            <div>
                                <button onClick=
                                    {handleClick}>
                                        Add Note
                                </button>
                            </div>
                        </div>
                    )}
      </Popup>
        </div>
        {notes.map((e) => (
          <div class="note-item">
            <div class="details">
              <p><b>{e.title}</b></p>
              <span>{e.description}</span>
            </div>
            <div class="bottom-content">
              <span>{e.date}</span>
            </div>
          </div>
        ))}
      </div>
    </body>
  );
}

const data = [
  {
      key: 0,
      title: "Html",
      desc: "HyperText MarkUp Language",
      date: "2015-10-2",
  },
  { key: 1, title: "CSS", desc: "StyleSheet", date: "2015-10-2", },
  {
      key: 2,
      title: "JavaScript",
      desc: "Scripting language for web",
      date: "2015-10-2",
  },
  {
      key: 3,
      title: "React",
      desc: "JavaScript framework",
      date: "2015-10-2",
  },
];

export default App;
